// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "lib/openzeppelin-contracts/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol"; 

/// @title BVP Staking Contract
/// @notice Allows users to stake BVP tokens with fixed lock periods to gain tier-based access privileges.
/// @dev This contract supports three staking durations (3m, 6m, 12m) and maps stake amounts to predefined tiers.
contract BVPStaking is ReentrancyGuard {
    IERC20 public immutable bvpToken;

    struct Stake {
        uint256 amount;
        uint256 timestamp;
        uint256 lockTime;
        bool unlocked;
    }

    mapping(address => Stake) private stakes;

    uint256 public constant LOCK_TIME_3M  = 90 days;
    uint256 public constant LOCK_TIME_6M  = 180 days;
    uint256 public constant LOCK_TIME_12M = 365 days;

    uint256 private constant TH_BRONZE   = 20_000   * 1e18;
    uint256 private constant TH_SILVER   = 100_000  * 1e18;
    uint256 private constant TH_GOLD     = 500_000  * 1e18;
    uint256 private constant TH_PLATINUM = 1_000_000 * 1e18;
    uint256 private constant TH_DIAMOND  = 2_000_000 * 1e18;

    event Staked(address indexed user, uint256 amount, uint256 lockTime, uint256 unlockAt);
    event Unlocked(address indexed user, uint256 when);
    event Unstaked(address indexed user, uint256 amount);

    constructor(address _bvpToken) {
        require(_bvpToken != address(0), "Zero token");
        bvpToken = IERC20(_bvpToken);
    }

    function _stake(uint256 amount, uint256 lockTime) internal nonReentrant {
        require(amount > 0, "Zero amount");

        Stake storage s = stakes[msg.sender];
        require(s.amount == 0, "Already staked");

        s.amount = amount;
        s.timestamp = block.timestamp;
        s.lockTime = lockTime;
        s.unlocked = false;

        require(bvpToken.transferFrom(msg.sender, address(this), amount), "TRANSFER_FROM_FAILED");

        emit Staked(msg.sender, amount, lockTime, block.timestamp + lockTime);
    }

    function stake3Months(uint256 amount) external {
        _stake(amount, LOCK_TIME_3M);
    }

    function stake6Months(uint256 amount) external {
        _stake(amount, LOCK_TIME_6M);
    }

    function stake12Months(uint256 amount) external {
        _stake(amount, LOCK_TIME_12M);
    }

    function unlock() external nonReentrant {
        Stake storage s = stakes[msg.sender];
        require(s.amount > 0, "No stake");
        require(!s.unlocked, "Already unlocked");
        require(block.timestamp >= s.timestamp + s.lockTime, "Still locked");

        s.unlocked = true;
        emit Unlocked(msg.sender, block.timestamp);
    }

    function unstake() external nonReentrant {
        Stake memory s = stakes[msg.sender];
        require(s.unlocked, "Not unlocked");

        delete stakes[msg.sender];
        require(bvpToken.transfer(msg.sender, s.amount), "TRANSFER_FAILED");

        emit Unstaked(msg.sender, s.amount);
    }

    function getStake(address user)
        external
        view
        returns (
            uint256 amount,
            uint256 timestamp,
            uint256 lockTime,
            bool unlocked,
            uint256 unlockAt
        )
    {
        Stake storage s = stakes[user];
        amount = s.amount;
        timestamp = s.timestamp;
        lockTime = s.lockTime;
        unlocked = s.unlocked;
        unlockAt = s.timestamp + s.lockTime;
    }

    function getTierCode(address user) public view returns (uint8) {
        uint256 a = stakes[user].amount;
        if (a >= TH_DIAMOND) return 5;
        if (a >= TH_PLATINUM) return 4;
        if (a >= TH_GOLD) return 3;
        if (a >= TH_SILVER) return 2;
        if (a >= TH_BRONZE) return 1;
        return 0;
    }

    function getTierName(address user) external view returns (string memory) {
        uint8 code = getTierCode(user);
        if (code == 1) return "Bronze";
        if (code == 2) return "Silver";
        if (code == 3) return "Gold";
        if (code == 4) return "Platinum";
        if (code == 5) return "Diamond";
        return "None";
    }

}

contracts/BVPToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Capped.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";

/// @title Big Vision Pictures Token (BVP)
/// @notice ERC-20 token with capped supply, fixed allocations, EIP-2612 permits, and anti-whale limits.
/// @dev Decentralized/no-admin: there are **no** owner roles or setters. Exclusions are fixed at deploy.
///      No burn: does not include ERC20Burnable and provides no burn path.
contract BVPToken is ERC20, ERC20Capped, ERC20Permit {
    // ---- Limits (immutable after deploy) ----
    uint256 public immutable maxTx;     // per-transfer ceiling
    uint256 public immutable maxWallet; // per-wallet holding ceiling

    // ---- Exclusions (fixed at deploy; no mutability) ----
    mapping(address => bool) public isTxLimitExcluded;     // sender bypasses maxTx
    mapping(address => bool) public isWalletLimitExcluded; // recipient bypasses maxWallet

    /// @param publicSale_        allocation recipient (30%)
    /// @param operations_        allocation recipient (20%)
    /// @param presale_           allocation recipient (10%)
    /// @param foundersAndTeam_   allocation recipient (10%)
    /// @param marketing_         allocation recipient (15%)
    /// @param advisors_          allocation recipient (5%)
    /// @param treasury_          allocation recipient (5%)
    /// @param liquidity_         allocation recipient (5%) â€“ typically LP/AMM wallet
    constructor(
        address publicSale_,
        address operations_,
        address presale_,
        address foundersAndTeam_,
        address marketing_,
        address advisors_,
        address treasury_,
        address liquidity_
    )
        ERC20("Big Vision Pictures Token", "BVP")
        ERC20Capped(1_000_000_000 ether)               // 1,000,000,000 * 1e18
        ERC20Permit("Big Vision Pictures Token")
    {
        // Set anti-whale ceilings (can tune constants here, they are immutable after deploy)
        maxTx = 10_000_000 ether;     // 10,000,000 BVP
        maxWallet = 20_000_000 ether; // 20,000,000 BVP

        // ---- Initial allocations (sum to 100% of cap) ----
        _mint(publicSale_,       cap() * 30 / 100);
        _mint(operations_,       cap() * 20 / 100);
        _mint(presale_,          cap() * 10 / 100);
        _mint(foundersAndTeam_,  cap() * 10 / 100);
        _mint(marketing_,        cap() * 15 / 100);
        _mint(advisors_,         cap() *  5 / 100);
        _mint(treasury_,         cap() *  5 / 100);
        _mint(liquidity_,        cap() *  5 / 100);

        // ---- Fixed exclusions (no setters; encoded policy) ----
        // Liquidity/operational wallets often need to bypass maxTx & maxWallet.
        isTxLimitExcluded[publicSale_] = true;
        isTxLimitExcluded[operations_] = true;
        isTxLimitExcluded[treasury_]   = true;
        isTxLimitExcluded[liquidity_]  = true;

        isWalletLimitExcluded[publicSale_] = true;
        isWalletLimitExcluded[operations_] = true;
        isWalletLimitExcluded[treasury_]   = true;
        isWalletLimitExcluded[liquidity_]  = true;
    }

    // -----------------------------
    // Anti-whale enforcement
    // -----------------------------
    /// @dev Enforce:
    ///      - maxTx on the *sender* unless sender is tx-excluded
    ///      - maxWallet on the *recipient* unless recipient is wallet-excluded
    ///      - mints/burns are not subject to limits (no burn path provided in this contract)
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        super._beforeTokenTransfer(from, to, amount);

        // Skip checks for mint/burn
        if (from == address(0) || to == address(0)) {
            return;
        }

        // Per-transfer ceiling
        if (!isTxLimitExcluded[from]) {
            require(amount <= maxTx, "TX_LIMIT");
        }

        // Per-wallet holding ceiling (post-transfer balance)
        if (!isWalletLimitExcluded[to]) {
            require(balanceOf(to) + amount <= maxWallet, "WALLET_LIMIT");
        }
    }

    // -----------------------------
    // Required override for ERC20 + ERC20Capped
    // -----------------------------
    function _mint(address account, uint256 amount)
        internal
        override(ERC20, ERC20Capped)
    {
        super._mint(account, amount);
    }
}

contracts/Lock.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.19;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

contract Lock {
    /// @notice Timestamp after which the funds can be withdrawn
    /// @dev Marked as immutable to save gas (set once during construction)
    uint public immutable unlockTime;

    /// @notice Owner of the contract, authorized to withdraw after unlock
    /// @dev Marked as immutable for gas efficiency
    address payable public immutable owner;

    event Withdrawal(uint amount, uint when);

    constructor(uint _unlockTime) payable {
        require(
            block.timestamp < _unlockTime,
            "Unlock time should be in the future"
        );

        unlockTime = _unlockTime;
        owner = payable(msg.sender);
    }

    function withdraw() public {
        // console.log("Unlock time is %o and block timestamp is %o", unlockTime, block.timestamp);

        require(block.timestamp >= unlockTime, "You can't withdraw yet");
        require(msg.sender == owner, "You aren't the owner");

        emit Withdrawal(address(this).balance, block.timestamp);
        owner.transfer(address(this).balance);
    }
}

